import React, { Component } from 'react'
import DataView from './DataView'

export default class CrossTabView extends Component{
    render() {
        console.log(this.props.data);
        return <DataView 
                    elementId = "crosstab" 
                    fields = { this.props.fields }
                    data = { this.props.data }
                    selectedGroup = { this.props.selectedGroup }
                    selectedQuestion = { this.props.selectedQuestion }
                    renderFunction = { this.renderCrossTabs }
                />
    }

    renderCrossTabs(elementId, selectedGroup, selectedQuestion, data, fields) {
        let cleanData = data.map((x) => { return {group : x[selectedGroup], response : x[selectedQuestion]} });
        if(fields[selectedQuestion].numeric) {
            $(`#${elementId}`).pivot(cleanData, 
                {
                    rows : ["group"],
                    aggregator: $.pivotUtilities.aggregators["Average"](["response"])
                });
        }
        else {
            $(`#${elementId}`).pivot(cleanData, 
                {
                    rows : ["group"],
                    cols: ["response"],
                    aggregator: $.pivotUtilities.aggregators["Count"]()
                });
        }
    }
}
