import React, { Component } from 'react';
import './App.css';
import {groups, surveys} from './config/fields.json'
import NavigationBar from './components/NavigationBar'
import DataView from './components/DataView'
import CrossTabView from './components/CrossTabView';

class App extends Component {
  constructor() {
    super(); 
    this.state = {
      surveyData : null,
      selectedGroup : null,
      selectedQuestion : null
    }
  }

  render() {
    return (
      <div className="App">
        <NavigationBar surveys={surveys}/>
        <CrossTabView 
          fields = {surveys.income_survey} 
          data = { this.state.surveyData }
          selectedGroup = { this.state.selectedGroup }
          selectedQuestion = { this.state.selectedQuestion } 
        />
      </div>
    );
  }

  componentDidMount() {
    d3.csv(`${process.env.PUBLIC_URL}/data_income_survey.csv`).then((data) => {
      this.setState({
        surveyData : data,
        selectedGroup : "income",
        selectedQuestion : "Q06006_en"
      })
    });
  }
}

export default App;
